                   . "," . $student->getId()                    . "," . $student->getTotalPoints()                    . ",'" . $student->getFullName()                    . "','" . $student->getBirthdate()                    . "','" . $student->getPhoneNumber()                    . "','" . $student->getEmail()                    . "', [";                //Finding all the parents of the student                foreach ($student->getAttendedGroupsInfolist() as $attended_group) {                    if ($attended_group['completion_date'] == null) {                        //Enrolled courses (Group id|End date) of the student                        $group = $attended_group['group'];                        echo "'" . $group['course_title'];                        echo " ( " . $group['id'] . " | " . $group['end_date'] . " )',";                    }                }                echo "],";                echo "[";                //Completed courses (Completion date) of the student                //Finding all the parents of the student                foreach ($student->getAttendedGroupsInfolist() as $attended_group) {                    if ($attended_group['completion_date'] != null) {                        $group = $attended_group['group'];                        echo "'" . $group->getCourse()->getTitle();                        echo " ( " . $attended_group['completion_date'] . " )',";                    }                }                echo "],";                echo "[";                //Parents of the student                //Finding all the parents of the student                foreach ($allParents as $parent) {                    foreach ($parent->getStudents() as $child_student) {                        if ($student->getId() == $child_student->getId()) {                            echo "'" . $parent->getFullName() .                                " (" . $parent->getPhoneNumber() . ")',";                        }                    }                }                echo "]],\n";            endforeach; ?>    ];    $(document).ready(function () {        $('#datatables, #datatables2').DataTable({            pagingType: "full_numbers",            lengthMenu: [                [25, 50, 100, -1],                [25, 50, 100, "All"]            ],            responsive: true,            language: {                search: "_INPUT_",                searchPlaceholder: "Search"            }        });        /*$('#students_datatables').DataTable({            data: all_students,            columns: [                {title: "#"},                {title: "lol Student id"},                {title: "Total points"},                {title: "Full name"},                {title: "Birthdate"},                {title: "Phone number"},                {title: "Email: "},                {title: "Enrolled courses (Group id|End date): "},                {title: "Completed courses (Completion date): "},                {title: "Parent (phone number): "}            ]        });*/        $('#parents_modal_datatables').DataTable({            "pagingType": "full_numbers",            "lengthMenu": [                [10, 25, 50, -1],                [10, 25, 50, "All"]            ],            responsive: true,            language: {                search: "_INPUT_",                searchPlaceholder: "Search",            }        });    });</script><script type="text/javascript">    $(document).ready(function () {        $('#summernote').summernote({            height: 400,            placeholder: 'Write here ...',            focus: false,            airMode: false,            dialogsInBody: true,            dialogsFade: true,            disableDragAndDrop: false,        });    });</script><script type="text/javascript">    $(document).ready(function () {        var timeslot_choosen = false;        $('#choose_timeslot_table tr').click(function (event) {            if (!timeslot_choosen) {                $('#choose_timeslot_table tr').each(function (value) {                    $(this).removeClass("success");                    $(this).addClass("hidden");                });                $(this).addClass("success");                $(this).removeClass("hidden");                $("input[name='selected_timeslot_id']").val($(this).find("input").val());            } else {                $('#choose_timeslot_table tr').each(function (value) {                    $(this).removeClass("success");                    $(this).removeClass("hidden");                });                $("input[name='selected_timeslot_id']").val("");            }            timeslot_choosen = !timeslot_choosen;        });        $("div[id='select_classroom'] ul[role='listbox'] > li").click(function (event) {            //NOTE: the text of selected element will contain the word 'done' at the end            $("input[name='selected_classroom']").val($(this).text().slice(0, -6));        });        var selected_primary_instructors = Array();        $("div[id='select_primary_instructor'] ul[role='listbox'] > li").click(function (event) {            var value = $(this).text().split(" - ");            value = value[0];            var exists = false;            var new_list = Array();            while (selected_primary_instructors.length > 0) {                var temp = selected_primary_instructors.pop();                if (temp != value) {                    new_list.push(temp);                } else {                    exists = true;                }            }            if (!exists) {                new_list.push(value);            }            selected_primary_instructors = new_list;            delete new_list;            //NOTE: the text of selected element will contain the word 'done' at the end            $("input[name='selected_primary_instructors']").val(selected_primary_instructors.join(','));        });        var selected_secondary_instructors = Array();        $("div[id='select_secondary_instructor'] ul[role='listbox'] > li").click(function (event) {            var value = $(this).text().split(" - ");            value = value[0];            var exists = false;            var new_list = Array();            while (selected_secondary_instructors.length > 0) {                var temp = selected_secondary_instructors.pop();                if (temp != value) {                    new_list.push(temp);                } else {                    exists = true;                }            }            if (!exists) {                new_list.push(value);            }            selected_secondary_instructors = new_list;            delete new_list;            //NOTE: the text of selected element will contain the word 'done' at the end            $("input[name='selected_secondary_instructors']").val(selected_secondary_instructors.join(','));        });        var selected_students = Array();        $('.waitlist tr').click(function (event) {            if ($(this).attr('thead') != 'true') {                $(this).toggleClass('success');                var value = $(this).find('td[id]').text();                var exists = false;                var new_list = Array();                while (selected_students.length > 0) {                    var temp = selected_students.pop();                    if (temp != value) {                        new_list.push(temp);                    } else {                        exists = true;                    }                }                if (!exists) {                    new_list.push(value);                }                selected_students = new_list;                delete new_list;                //NOTE: the text of selected element will contain the word 'done' at the end                $("input[name='selected_students']").val(selected_students.join(','));            }        });        $('#parents_modal_datatables tr').click(function (event) {            //alert($(this.cells[2]).text());            $('input[name=student_parent_id]').attr("value", $(this.cells[1]).text());            $('#select_parent').html($(this.cells[1]).text() + " - " + $(this.cells[2]).text());            $('#select_parent').removeClass('btn-warning');            $('#select_parent').addClass('btn-success');            $('#parents_modal').modal('toggle');        });        <?php if(isset($allCourses)): ?>        var lastSelect = 'none';        //Listen for a click on selectCourse of the dropdown items        $("div[id='select_course'] ul[role='listbox'] > li").click(function () {            //Get the selected course id value from the text            //NOTE: the text of selected element will contain the word 'done' at the end            var value = $(this).text() + "";            var temp = value.split(" - ");            value = temp[0];            /*Show required courses for a selected course id*/            var courses = {                <?php foreach ($allCourses as $course) {                    echo $course->getCourseId() . ":$('#" . $course->getCourseId() . "'),\n";                }                ?>            };            $("#required_courses_title").removeClass('hidden');            //Put the retrieved value into the hidden input            if (lastSelect != 'none')                courses[lastSelect].addClass('hidden');            courses[value].removeClass('hidden');            /*Adding value to a form element*/            lastSelect = value;            $("input[name='selected_course_id']").val(value);            // var table = $('#datatables').DataTable();            // table.rows().every( function ( rowIdx, tableLoop, rowLoop ) {            // } );            // var c = 0;            // var trs = document.querySelectorAll('#datatables tbody tr');            // for (var i = 0; i < trs.length(); i++)            // alert(trs[i].innerHTML);        });        <?php endif; ?>    });</script></html>